    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        String PID=T1.getText();
        try {
            Class.forName("com.mysql.jdbc.Driver");
            Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/online","root","password");
            Statement S=con.createStatement();
            int opt=JOptionPane.showConfirmDialog(null,"Are You Sure To Delete This Record?");
           if(opt==JOptionPane.YES_OPTION)
           { String query="DELETE from amazon where Product_ID='"+PID+"';";
            S.executeUpdate(query);
            JOptionPane.showMessageDialog(null,"Record Deleted");
           }
           else
               JOptionPane.showMessageDialog(null,"Can't Delete");
            S.close();
            con.close();
        } catch(Exception E) {
      JOptionPane.showMessageDialog(null,"Could Not Delete, Please Try Again");
        }
}//GEN-LAST:event_jButton4ActionPerformed

