    private void jButton10ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton10ActionPerformed
        // TODO add your handling code here:
          String PID=T1.getText();
        {
       String query="Select * from amazon where Product_ID='"+PID+"';";
        try
        {
            Class.forName("com.mysql.jdbc.Driver");
         Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/online","root","password");
         Statement S=con.createStatement();
         ResultSet R=S.executeQuery(query);
         if(R.next())
         {
         String PN=R.getString("Product_Name");
         String B=R.getString("Brand");
         int MRP=R.getInt("MRP");
         int PO=R.getInt("Price_Offered");
         int DT=R.getInt("Delivery_Time");
         String MOP=R.getString("Mode_Of_Payment");
         T2.setText(PN);
         T3.setText(B);
         T4.setText(""+MRP);
         T5.setText(""+PO);
         T6.setText(""+DT);
         T7.setText(""+MOP);
         }
        
         R.close();
         S.close();
         con.close();
                }
        catch(Exception E)
        {
            JOptionPane.showMessageDialog(null,"Could Not Search, Please Try Again");
        }}
    }//GEN-LAST:event_jButton10ActionPerformed

